<?phpif ($_SERVER['REQUEST_METHOD'] == 'POST') {    $conn = new mysqli("localhost", "root", "", "fitnesshub");    if ($conn->connect_error) {        die("<div class='error-box'>Connection failed: " . $conn->connect_error . "</div>");    }    $name = htmlspecialchars($_POST['name']);    $age = (int)$_POST['age'];    $gender = htmlspecialchars($_POST['gender']);    $email = htmlspecialchars($_POST['email']);    $height = (float)$_POST['height'];    $weight = (float)$_POST['weight'];    $bmi = (float)$_POST['bmi'];    $target = (float)$_POST['target_weight'];    $workout_place = htmlspecialchars($_POST['workout_place']);    $date = $_POST['date'];    $stmt = $conn->prepare("INSERT INTO details (NAME, AGE, GENDER, EMAIL, HEIGHT, WEIGHT, BMI, TARGET_WEIGHT, WORKOUT_PLACE, DATE) VALUES (?, ?, ?, ?, ?, ?, ?, ?, ?, ?)");    $stmt->bind_param("sissddddss", $name, $age, $gender, $email, $height, $weight, $bmi, $target, $workout_place, $date);    if ($stmt->execute()) {        echo "<div class='success-box'>Data Saved Successfully!</div>";        echo "<script>setTimeout(() => { window.location.href = 'trackers.php'; }, 1000);</script>";    } else {        echo "<div class='error-box'>Error: " . $stmt->error . "</div>";    }    $stmt->close();    $conn->close();}?><html><head>    <title>Personal Details</title>    <style>        body {            font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;            background: linear-gradient(rgba(0, 0, 0, 0.6), rgba(0, 0, 0, 0.6)),            url('details.jpg') no-repeat center center/cover;			min-height: 100vh;			margin: 0;			color:white;			display:flex;            justify-content: center;			align-items:flex-start;            padding: 50px 0;			overflow-y:hidden;			overflow-y:auto;			box-sizing:border-box;        }        form {            width: 90%;            max-width: 500px;            padding: 35px;            background: rgba(28, 28, 28, 0.85);            border-radius: 20px;            box-shadow: 0 8px 24px rgba(255, 215, 0, 0.4);            text-align: left;            backdrop-filter: blur(10px);			margin-bottom: 50px;        }        h2 {            color: gold;            text-transform: uppercase;            margin-bottom: 25px;            text-align: center;            border-bottom: 2px solid gold;            padding-bottom: 10px;        }        label {            display: block;            margin-top: 15px;            margin-bottom: 5px;            font-weight: 600;        }        input, select {            width: 100%;            padding: 12px;            font-size: 14px;            border: 1px solid #777;            border-radius: 8px;            background: #f8f8f8;            color: #111;        }        input:focus, select:focus {            border-color: gold;            box-shadow: 0 0 8px gold;            outline: none;        }        button {            margin-top: 25px;            background: gold;            color: black;            padding: 14px;            border: none;            border-radius: 8px;            width: 100%;            font-size: 16px;            font-weight: bold;            cursor: pointer;        }        button:hover {            background: #fffbe0;        }        .success-box, .error-box {            padding: 12px;            margin: 15px auto;            border-radius: 8px;            width: 90%;            font-weight: bold;        }        .success-box {            background: #d4edda;            color: #155724;        }        .error-box {            background: #f8d7da;            color: #721c24;        }        @media (max-width: 600px) {            form {                padding: 25px;            }        }    </style>    <script>        function calculateBMI() {            const height = parseFloat(document.getElementById('height').value);            const weight = parseFloat(document.getElementById('weight').value);            const bmiField = document.getElementById('bmi');            if (height > 0 && weight > 0) {                const heightInMeters = height / 100;                const bmi = weight / (heightInMeters * heightInMeters);                bmiField.value = bmi.toFixed(2);            } else {                bmiField.value = '';            }        }    </script></head><body><form action="details.php" method="POST" oninput="calculateBMI()">    <h2>Personal Details</h2>    <label for="name">Full Name</label>    <input type="text" name="name" id="name" placeholder="Enter your name" required>    <label for="age">Age</label>    <input type="number" name="age" id="age" placeholder="Enter your age" min="0" required>    <label for="gender">Gender</label>    <select name="gender" id="gender" required>        <option value="" disabled selected>Select your gender</option>        <option value="Male">Male</option>        <option value="Female">Female</option>        <option value="Other">Other</option>    </select>    <label for="email">Email</label>    <input type="email" name="email" id="email" placeholder="Enter your email" required>    <label for="height">Height (cm)</label>    <input type="number" name="height" id="height" placeholder="Height in cm" min="0" required>    <label for="weight">Weight (kg)</label>    <input type="number" name="weight" id="weight" placeholder="Weight in kg" min="0" required>    <label for="bmi">BMI</label>    <input type="number" name="bmi" id="bmi" placeholder="Your BMI" step="0.01" readonly required>    <label for="target_weight">Target Weight (kg)</label>    <input type="number" name="target_weight" id="target_weight" placeholder="Target weight in kg" min="0" required>    <label for="workout_place">Workout Location</label>    <select name="workout_place" id="workout_place" required>        <option value="" disabled selected>Select your workout location</option>        <option value="Home">At Home</option>        <option value="Gym">At Gym</option>    </select>    <label for="date">Date</label>    <input type="date" name="date" id="date" required>    <button type="submit">Save</button></form></body></html>